pub enum Inventory {
    Error,
    Transaction(Txid),
    Block(BlockHash),
    WTx(Wtxid),
    WitnessTransaction(Txid),
    WitnessBlock(BlockHash),
    Unknown {
            inv_type: u32,
            hash: [u8; ],
        },
}

pub enum Error {
    Io(io::Error),
    SocketMutexPoisoned,
    SocketNotConnectedToPeer,
}

pub enum Error {
    Base58(base58::Error),
    Secp256k1(secp256k1::Error),
    InvalidKeyPrefix(u8),
    Hex(hex::Error),
}

pub enum EcdsaSigError {
    HexEncoding(hex::Error),
    NonStandardSighashType(u32),
    EmptySignature,
    Secp256k1(secp256k1::Error),
}

pub enum SchnorrSigError {
    InvalidSighashType(u8),
    Secp256k1(secp256k1::Error),
    InvalidSchnorrSigSize(usize),
}

pub enum Payload {
    PubkeyHash(PubkeyHash),
    ScriptHash(ScriptHash),
    WitnessProgram {
            version: WitnessVersion,
            program: Vec<u8>,
        },
}

pub enum ChildNumber {
    Normal {
            index: u32,
        },
    Hardened {
            index: u32,
        },
}

pub enum MerkleBlockError {
    MerkleRootMismatch,
    NoTransactions,
    TooManyTransactions,
    BadFormat(String),
}

pub enum MessageSignatureError {
    InvalidLength,
    InvalidEncoding(secp256k1::Error),
    InvalidBase64,
    UnsupportedAddressType(AddressType),
}

