pub enum Prevouts<'u, T> where T: 'u + Borrow<TxOut> {
    One(usize, T),
    All(&'u [T]),
}

pub enum Error {
    Io(io::ErrorKind),
    IndexOutOfInputsBounds {
            index: usize,
            inputs_size: usize,
        },
    SingleWithoutCorrespondingOutput {
            index: usize,
            outputs_size: usize,
        },
    PrevoutsSize,
    PrevoutIndex,
    PrevoutKind,
    WrongAnnex,
    InvalidSighashType(u32),
}

pub enum Error {
    Encode(encode::Error),
    Network(network::Error),
    BlockBadProofOfWork,
    BlockBadTarget,
}

pub enum Error {
    Io(io::Error),
    Psbt(psbt::Error),
    UnexpectedNetworkMagic {
            expected: u32,
            actual: u32,
        },
    OversizedVectorAllocation {
            requested: usize,
            max: usize,
        },
    InvalidChecksum {
            expected: [u8; ],
            actual: [u8; ],
        },
    NonMinimalVarInt,
    UnknownNetworkMagic(u32),
    ParseFailed(&'static str),
    UnsupportedSegwitFlag(u8),
}
